'use client';

import { useAuth } from '@/hooks/useAuth';
import { Anchor, Button, Checkbox, Container, Paper, PasswordInput, Text, TextInput, Title } from '@mantine/core';
import { useForm } from '@mantine/form';

export default function Register() {
  const { signup, isSignupPending } = useAuth();

  const form = useForm({
    initialValues: {
      username: '',
      password: '',
      nickname: '',
      terms: false,
    },
    validate: {
      username: (value) => (value.length < 4 ? 'Username must be at least 4 characters' : null),
      password: (value) => (value.length < 6 ? 'Password must be at least 6 characters' : null),
      nickname: (value) => (value.length < 2 ? 'Nickname is too short' : null),
      terms: (value) => (!value ? 'You must agree to terms' : null),
    },
  });

  const handleSubmit = (values: typeof form.values) => {
    const { terms, ...data } = values;
    signup(data);
  };

  return (
    <Container size="xs" py={120}>
      <div className="text-center mb-8">
         <Title order={1} className="text-3xl font-black text-white mb-2 tracking-tight">계정 만들기</Title>
         <Text c="gray.4">지금 가입하고 투표에 참여하세요</Text>
      </div>

      <Paper radius="lg" p="xl" bg="#1A1B1E" className="border border-white/5 shadow-2xl">
        <form onSubmit={form.onSubmit(handleSubmit)}>
          <TextInput
            label="사용자 이름"
            placeholder="사용하실 아이디를 입력하세요"
            required
            mb="md"
            {...form.getInputProps('username')}
            classNames={{ input: 'bg-white/5 border-white/10' }}
          />
          <TextInput
            label="닉네임"
            placeholder="표시될 이름을 입력하세요"
            required
            mb="md"
            {...form.getInputProps('nickname')}
            classNames={{ input: 'bg-white/5 border-white/10' }}
          />
          <PasswordInput
            label="비밀번호"
            placeholder="비밀번호를 입력하세요"
            required
            mb="md"
            {...form.getInputProps('password')}
            classNames={{ input: 'bg-white/5 border-white/10' }}
          />
          <Checkbox 
            label="이용 약관에 동의합니다"
            mb="xl"
            color="violet"
            {...form.getInputProps('terms', { type: 'checkbox' })}
          />
          <Button 
            fullWidth 
            size="lg" 
            radius="md"
            type="submit"
            color="violet"
            loading={isSignupPending}
            className="shadow-lg shadow-violet-900/20"
          >
            가입하기
          </Button>
        </form>
      </Paper>

      <Text ta="center" mt="xl" size="sm" c="dimmed">
        이미 계정이 있으신가요?{' '}
        <Anchor href="/login" size="sm" c="violet.4" fw={600}>
          로그인
        </Anchor>
      </Text>
    </Container>
  );
}

'use client';

import { ApiResponse, categoryApi, CreatePollRequest, CreatePollResponse, pollApi, PollType, PollVisibility } from '@/lib/api';
import { pollKeys, queryKeys } from '@/lib/queryKeys';
import { ActionIcon, Button, Container, Grid, Group, NumberInput, Paper, SegmentedControl, Select, Stack, Switch, Text, Textarea, TextInput, Title } from '@mantine/core';
import { useForm } from '@mantine/form';
import { notifications } from '@mantine/notifications';
import { IconPlus, IconTrash } from '@tabler/icons-react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { AxiosError } from 'axios';
import dayjs from 'dayjs';
import { useRouter } from 'next/navigation';

export default function CreateVotePage() {
  const router = useRouter();
  const queryClient = useQueryClient();

  const { data: categories } = useQuery({
    queryKey: queryKeys.categories.all.queryKey,
    queryFn: categoryApi.getAll,
  });

  const form = useForm({
    initialValues: {
      title: '',
      description: '',
      categoryId: null as string | null,
      pollType: PollType.VOTE,
      options: ['찬성', '반대'],
      durationDays: 7,
      allowAnonymous: true,
      allowChange: true,
      maxSelections: 1,
      visibility: PollVisibility.PUBLIC
    },
    validate: {
      title: (v) => (v.length < 5 ? '제목은 5자 이상이어야 합니다.' : null),
      options: (v) => {
        if (v.some(opt => opt.trim() === '')) return '모든 옵션을 입력해야 합니다.';
        if (v.length < 2) return '최소 2개의 옵션이 필요합니다.';
        return null;
      }
    }
  });

  const createMutation = useMutation({
    mutationFn: (data: CreatePollRequest) => pollApi.create(data),
    onSuccess: (res: ApiResponse<CreatePollResponse>) => {
        notifications.show({ title: '투표 생성 완료!', message: '준비 되었습니다.', color: 'green' });
        queryClient.invalidateQueries({ queryKey: pollKeys.all.queryKey });
        const pollId = res.data?.pollId;
        if (pollId) {
            router.push(`/vote/${pollId}`);
        } else {
            router.push('/');
        }
    },
    onError: (err: AxiosError<ApiResponse<null>>) => {
        notifications.show({ 
            title: '생성 실패', 
            message: (err.response?.data as ApiResponse<null>)?.message || '투표를 만들 수 없습니다.', 
            color: 'red' 
        });
    }
  });

  const handleAddOption = () => {
    form.insertListItem('options', '');
  };

  const handleRemoveOption = (index: number) => {
    if (form.values.options.length <= 2) {
        notifications.show({ color: 'red', message: '최소 2개의 옵션이 필요합니다.' });
        return;
    }
    form.removeListItem('options', index);
  };

  const handleSubmit = async (values: typeof form.values) => {
    const endsAt = dayjs().add(values.durationDays, 'day').toISOString();
    
    const requestData: CreatePollRequest = {
        title: values.title,
        description: values.description || null, // Kept original logic for description being optional
        categoryId: values.categoryId ? Number(values.categoryId) : null,
        pollType: values.pollType as PollType, // Use form value
        visibility: values.visibility,
        allowAnonymous: values.allowAnonymous,
        allowChange: values.allowChange,
        maxSelections: values.maxSelections,
        endsAt: endsAt,
        options: values.options
    };

    createMutation.mutate(requestData);
  };

  return (
    <Container size="sm" py={20}>
      <Title order={2} mb="xl" className="text-white">새로운 투표 만들기</Title>
      
      <Paper p="xl" radius="lg" bg="#1A1B1E">
        <Stack gap="lg">
             {/* Type Selection */}
              <div>
                <Text fw={600} mb="xs" c="gray.3">투표 유형</Text>
                <SegmentedControl
                    fullWidth
                    value={form.values.pollType} // Use form value
                    onChange={(v: string) => {
                        const newType = v as PollType;
                        form.setFieldValue('pollType', newType);
                        if (newType === PollType.VOTE) {
                            form.setFieldValue('options', ['찬성', '반대']);
                            form.setFieldValue('maxSelections', 1);
                        } else if (newType === PollType.RANK && form.values.options[0] === '찬성') {
                            form.setFieldValue('options', ['', '']);
                        }
                    }}
                    data={[
                        { label: '찬반 투표 (A vs B)', value: PollType.VOTE },
                        { label: '순위 투표 (리스트)', value: PollType.RANK },
                    ]}
                    color="violet"
                />
              </div>

             <form onSubmit={form.onSubmit(handleSubmit)}>
                <Stack gap="md">
                    <TextInput 
                        label="제목" 
                        placeholder={form.values.pollType === PollType.VOTE ? "예: 나는 민초파다" : "예: 다음 회식 메뉴는?"} 
                        required 
                        withAsterisk
                        {...form.getInputProps('title')}
                    />
                    
                    <Textarea 
                        label="상세 설명 (선택 사항)" 
                        placeholder="투표에 대한 추가 정보를 입력하세요" 
                        minRows={3}
                        {...form.getInputProps('description')}
                    />

                    <Select
                        label="카테고리"
                        placeholder="선택하세요"
                        data={categories?.map(c => ({ value: String(c.id), label: c.name })) || []}
                        {...form.getInputProps('categoryId')}
                        clearable
                        searchable
                    />

                    {/* Options Management */}
                    {form.values.pollType === PollType.RANK && (
                        <div>
                            <Group justify="space-between" mb="xs">
                                <Text fw={500} size="sm" c="gray.3">옵션</Text>
                                <Button variant="subtle" size="compact-xs" leftSection={<IconPlus size={14}/>} onClick={handleAddOption}>
                                    옵션 추가하기
                                </Button>
                            </Group>

                            <Stack gap="sm">
                                {form.values.options.map((_, index) => (
                                    <Group key={index} gap="xs">
                                        <TextInput 
                                            placeholder={`옵션 ${index + 1}`}
                                            className="flex-1"
                                            {...form.getInputProps(`options.${index}`)}
                                        />
                                        {form.values.options.length > 2 && (
                                            <ActionIcon color="red" variant="subtle" onClick={() => handleRemoveOption(index)}>
                                                <IconTrash size={16} />
                                            </ActionIcon>
                                        )}
                                    </Group>
                                ))}
                            </Stack>
                        </div>
                    )}

                    {form.values.pollType === PollType.VOTE && (
                         <div className="bg-white/5 p-4 rounded-lg border border-white/5">
                            <Text size="sm" c="gray.4" mb="xs" fw={500}>투표 옵션</Text>
                            <Group grow>
                                <Paper p="xs" ta="center" className="border border-blue-500/30 bg-blue-500/10">
                                    <Text size="sm" fw={700} c="blue.3">찬성</Text>
                                </Paper>
                                <Paper p="xs" ta="center" className="border border-cyan-500/30 bg-cyan-500/10">
                                    <Text size="sm" fw={700} c="cyan.3">반대</Text>
                                </Paper>
                            </Group>
                            <Text size="xs" c="dimmed" mt="sm">찬반 투표는 '찬성' 또는 '반대' 중에서 선택하는 방식으로 진행됩니다.</Text>
                         </div>
                    )}

                    <Grid>
                        <Grid.Col span={6}>
                            <NumberInput 
                                label="투표 기간 (일)" 
                                min={1} 
                                max={30} 
                                {...form.getInputProps('durationDays')} 
                            />
                        </Grid.Col>
                         <Grid.Col span={6}>
                            <NumberInput 
                                label="최대 선택 개수" 
                                min={1} 
                                max={form.values.pollType === PollType.VOTE ? 1 : 10} // Use form value
                                disabled={form.values.pollType === PollType.VOTE} // Use form value
                                {...form.getInputProps('maxSelections')} 
                            />
                        </Grid.Col>
                    </Grid>

                    <Group grow>
                        <Switch label="익명 투표 허용" {...form.getInputProps('allowAnonymous', { type: 'checkbox' })} />
                        <Switch label="투표 변경 허용" {...form.getInputProps('allowChange', { type: 'checkbox' })} />
                    </Group>

                    <Button type="submit" size="lg" fullWidth mt="xl" color="violet" loading={createMutation.isPending}>
                        투표 게시하기
                    </Button>
                </Stack>
             </form>
        </Stack>
      </Paper>
    </Container>
  );
}

'use client';

import { categoryApi } from '@/lib/api';
import { queryKeys } from '@/lib/queryKeys';
import { Code, Group, NavLink, ScrollArea, Text, UnstyledButton } from '@mantine/core';
import { IconFlame, IconHome, IconPlus, IconSettings, IconTrophy } from '@tabler/icons-react';
import { useQuery } from '@tanstack/react-query';
import Link from 'next/link';
import { usePathname } from 'next/navigation';

export function Navbar() {
  const pathname = usePathname();
  
  const { data: categories } = useQuery({
    queryKey: queryKeys.categories.all.queryKey,
    queryFn: categoryApi.getAll,
  });

  const isActive = (path: string) => pathname === path;

  return (
    <nav className="fixed top-0 left-0 w-[240px] h-full border-r border-white/5 bg-[#0A0A0B] flex flex-col z-40">
       {/* Logo Area */}
       <div className="h-[60px] flex items-center px-5 border-b border-white/5">
            <Link href="/" className="no-underline flex items-center gap-2 group">
                <div className="w-7 h-7 rounded-md bg-gradient-to-br from-violet-600 to-indigo-600 flex items-center justify-center text-white font-bold shadow-lg group-hover:scale-105 transition-transform">
                    V
                </div>
                <Text fw={700} size="lg" c="white">
                    Vote<span className="text-violet-400">Board</span>
                </Text>
            </Link>
       </div>

       {/* 메인 네비게이션 */}
       <ScrollArea className="flex-1">
            <div className="p-3 space-y-1">
                <NavLink
                    component={Link}
                    href="/"
                    label="홈"
                    leftSection={<IconHome size={18} />}
                    active={isActive('/')}
                    variant="light"
                    color="violet"
                    className="rounded-md font-medium"
                />
                <NavLink
                    component={Link}
                    href="/rankings"
                    label="인기 랭킹"
                    leftSection={<IconTrophy size={18} />}
                    active={isActive('/rankings')}
                    variant="light"
                    color="violet"
                    className="rounded-md font-medium"
                />
                 <NavLink
                    component={Link}
                    href="/battles"
                    label="투표 배틀"
                    leftSection={<IconFlame size={18} />}
                    active={isActive('/battles')}
                    variant="light"
                    color="violet"
                    className="rounded-md font-medium"
                />
            </div>

            <div className="p-3 mt-2 space-y-1">
                 <Text size="xs" fw={700} c="dimmed" tt="uppercase" px={12} mb={4}>설정</Text>
                 <NavLink
                    component={Link}
                    href="/category/manage"
                    label="카테고리 설정"
                    leftSection={<IconSettings size={18} />}
                    active={isActive('/category/manage')}
                    variant="light"
                    color="gray"
                    className="rounded-md font-medium"
                />
            </div>
       </ScrollArea>

       {/* 하단 액션 */}
       <div className="p-3 border-t border-white/5">
            <Link href="/vote/create" className="no-underline">
                <UnstyledButton className="w-full bg-gradient-to-r from-violet-600 to-indigo-600 hover:from-violet-500 hover:to-indigo-500 text-white p-3 rounded-lg shadow-lg flex items-center justify-center gap-2 transition-all">
                    <IconPlus size={18} />
                    <Text fw={600} size="sm">투표 만들기</Text>
                </UnstyledButton>
            </Link>
            
            <Group justify="space-between" mt="md" px={2}>
                 <Code className="bg-transparent text-xs text-gray-600 font-medium">v1.2.0</Code>
            </Group>
       </div>
    </nav>
  );
}

import { createTheme } from '@mantine/core';

export const theme = createTheme({
  primaryColor: 'violet',
  colors: {
    // Custom dark violet scale for background/UI
    dark: [
      '#C1C2C5',
      '#A6A7AB',
      '#909296',
      '#5C5F66',
      '#373A40',
      '#2C2E33',
      '#25262B', // 6: Main background
      '#1A1B1E', // 7: Card background
      '#141517',
      '#101113',
    ],
    // Vibrant Violet for accents
    violet: [
      '#F3F0FF',
      '#E5DBFF',
      '#D0BFFF',
      '#B197FC',
      '#9775FA',
      '#845EF7',
      '#7950F2',
      '#7048E8',
      '#6741D9',
      '#5F3DC4',
    ],
  },
  defaultRadius: 'md',
  fontFamily: 'Inter, -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica, Arial, sans-serif',
  headings: {
    fontFamily: 'Outfit, sans-serif',
    fontWeight: '700',
  },
  components: {
    Button: {
      defaultProps: {
        radius: 'md',
        fw: 600,
      },
      styles: {
        root: {
            transition: 'transform 0.1s ease',
        },
      }
    },
    Card: {
      defaultProps: {
        radius: 'lg',
        withBorder: true,
      },
      styles: (theme) => ({
        root: {
          backgroundColor: 'rgba(26, 27, 30, 0.6)', // Glass-like dark
          backdropFilter: 'blur(12px)',
          borderColor: 'rgba(255, 255, 255, 0.1)',
        },
      }),
    },
    Paper: {
        defaultProps: {
            radius: 'lg',
        },
        styles: (theme) => ({
            root: {
                backgroundColor: 'rgba(26, 27, 30, 0.6)',
                borderColor: 'rgba(255, 255, 255, 0.1)',
            }
        })
    },
    Modal: {
        styles: (theme) => ({
            content: {
                backgroundColor: '#1A1B1E',
                border: '1px solid rgba(255,255,255,0.1)',
            },
            header: {
                backgroundColor: '#1A1B1E',
            }
        })
    }
  },
});
